import React, {Fragment} from "react";
import { Link } from "react-router-dom";
const Posts = ({posts}) => {
    return (
        <div>
            <Link to="/posts/createpost"></Link>
            {
                posts.map((post) => {
                    const {description, location, price, title, isAuthor} = post;
                    return (
                        <>
                        <h3>{title}</h3>
                        <p>Description: {description}</p>
                        <p>Location: {location}</p>
                        <p>Price: {price}</p>
                        { isAuthor ? (
                            <>
                            <button>Edit</button>
                            <button>Delete</button>
                            <button>View</button>
                            </>
                        ) : (
                            <button>View</button>
                        )

                        }
                        </>
                    )
                })
            }
        </div>
    )
}

export default Posts
