import {React, useState} from "react";
import { createPost } from "../api";
const CreatePost = ({token}) => {
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [price, setPrice] = useState('')
    const [location, setLocation] = useState('')
    const [deliver, setDeliver] = useState(false)
    const newPost = {
        title: {title},
        description: {description},
        price: {price},
        location: {location},
        willDeliver: {deliver}
    }
    async function addPost() {
        const result = await createPost(token, newPost)
    }
    return (
        <form>
            <input type="text" placeholder="Enter Title" onChange={(event) => setTitle(event.target.value)}></input>
            <input type="text" placeholder="Enter Description" onChange={(event) => setDescription(event.target.value)}></input>
            <input type="text" placeholder="Enter Price in USD" onChange={(event) => setPrice(event.target.value)}></input>
            <input type="text" placeholder="Enter Location" onChange={(event) => setLocation(event.target.value)}></input>
            <input type="text" placeholder="Will you deliver? Enter True or False" onChange={(event) => setDeliver(event.target.value)}></input>
            <button onClick={() => addPost()}>Create New Post</button>
        </form>
    )
}
export default CreatePost
