import {React, useState} from "react";
import { useParams } from "react-router-dom";
import {updatePost, deletePosts} from "../api"


const EditPost = ({ posts, token, navigate, retrievePosts}) => {

    const { postID } = useParams();

    const [currentPost] = posts.filter(post => post._id === postID);
    const {title, description, location, price, willDeliver} = currentPost;
    console.log(postID)
    const [newTitle, setNewTitle] = useState(title)
    const [newDescription, setNewDescription] = useState(description)
    const [newLocation, setNewLocation] = useState(location)
    const [newPrice, setNewPrice] = useState(price)
    const [newWillDeliver, setNewWillDeliver] = useState(willDeliver)

    async function editPost() {
            const updatedPost = {
               token: token,
               title: newTitle,
               description: newDescription,
               location: newLocation,
               price: newPrice,
               willDeliver: newWillDeliver,
               _id: postID
            }
            await updatePost(updatedPost)
            retrievePosts();
            navigate('./posts')
        }
    return (
            <form onSubmit={(e) => {
                e.preventDefault();
                editPost();
            }}>
                <input type="text" placeholder={title} onChange={(e) => setNewTitle(e.target.value)}></input>
                <input type="text" placeholder={description} onChange={(e) => setNewDescription(e.target.value)}></input>
                <input type="text" placeholder={location} onChange={(e) => setNewLocation(e.target.value)}></input>
                <input type="text" placeholder={price} onChange={(e) => setNewPrice(e.target.value)}></input>
                <input type="text" placeholder={willDeliver.toString()} onChange={(e) => setNewWillDeliver(e.target.value)}></input>
                <button type='submit'>Edit Post</button>
                <button onClick={() => deletePosts(token, postID)}>Delete Post</button>


            </form>
        )

}
export default EditPost
