import React from "react";
const CreatePost = () => {
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [price, setPrice] = useState('')
    const [location, setLocation] = useState('')
    const [deliver, setDeliver] = useState(false)
    const newPost = {
        title: {title},
        description: {description},
        price: {price},
        location: {location},
        willDeliver: {deliver}
    }
    return (
        <form>
            <input type="text" placeholder="Enter Title" onChange={(event) => setTitle(event.target.value)}>Title</input>
            <input type="text" placeholder="Enter Description" onChange={(event) => setDescription(event.target.value)}>Description</input>
            <input type="text" placeholder="Enter Price in USD" onChange={(event) => setPrice(event.target.value)}>Price</input>
            <input type="text" placeholder="Enter Location" onChange={(event) => setLocation(event.target.value)}>Location</input>
            <input type="text" placeholder="Will you deliver? Enter True or False" onChange={(event) => setDeliver(event.target.value)}>Will Deliver</input>
        </form>
    )
}
export default CreatePost
