import  {React, Fragment, useEffect} from "react";
import { Link, useParams} from "react-router-dom";
import { deletePosts } from "../api";
const Posts = ({posts, token, retrievePosts, navigate}) => {
    useEffect(() => {
        retrievePosts()
    }, [token])

    const { postID } = useParams();
    posts.filter(post => post._id === postID);

    async function deletePost(token, postID){
       await deletePosts(token, postID);
       retrievePosts();
       navigate('./posts')
    }

    return (
        <div>
            {
                posts.map((post) => {
                    const {description, location, price, title, isAuthor, willDeliver, _id, active} = post;
                    return (
                    <div key={_id}>
                        <h3>{title}</h3>
                        <p>Description: {description}</p>
                        <p>Location: {location}</p>
                        <p>Price: {price}</p>
                        <p>Will Deliver: {willDeliver.toString()}</p>
                        <p>Is Active: {active.toString()}</p>
                        { 
                        isAuthor ? (
                            <Fragment>
                            <Link to={`/posts/edit-post/${_id}`}>Edit</Link>
                                <button onClick={(event) => {event.preventDefault() ; deletePost(token, postID)}}>Delete</button>
                            <Link to={`/posts/${_id}`}>View</Link>
                            </Fragment>
                        ) : (
                            <Link to={`/posts/${_id}`}>View</Link>
                        )

                        }
                    </div>
                    )
                })
            }
        </div>
    )
}

export default Posts
