import  {React, Fragment} from "react";
import { Link, useParams} from "react-router-dom";
import { deletePosts } from "../api";
const Posts = ({posts, token, retrievePosts}) => {
    const { postID } = useParams();

    const [currentPost] = posts.filter(post => post._id === postID);
    async function deletePost(){
       await deletePosts({token}, postID);
       retrievePosts();
    }

    return (
        <div>
            {
                posts.map((post) => {
                    const {description, location, price, title, isAuthor, willDeliver, _id} = post;
                    return (
                    <div key={_id}>
                        <h3>{title}</h3>
                        <p>Description: {description}</p>
                        <p>Location: {location}</p>
                        <p>Price: {price}</p>
                        <p>Will Deliver: {willDeliver.toString()}</p>
                        { 
                        isAuthor ? (
                            <Fragment>
                            <Link to={`/posts/edit-post/${_id}`}>Edit</Link>
                            <button onClick={(event) => {event.preventDefault() ; deletePost()}}>Delete</button>
                            <Link to={`/posts/${_id}`}>View</Link>
                            </Fragment>
                        ) : (
                            <Link to={`/posts/${_id}`}>View</Link>
                        )

                        }
                    </div>
                    )
                })
            }
        </div>
    )
}

export default Posts
