import {React, useState} from "react";
import { createPost } from "../api";
import "../style.css"
const CreatePost = ({token, retrievePosts, navigate}) => {
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [price, setPrice] = useState('')
    const [location, setLocation] = useState('')
    const [willDeliver, setWillDeliver] = useState(false)
    const newPost = {
        title,
        description,
        price,
        location,
        willDeliver
    }
    async function addPost() {
        await createPost(token, newPost);
        retrievePosts();
        navigate('./posts')


    }
    return (
        <form onSubmit={(e) => {
            e.preventDefault();
            addPost();
        }}>
            <input type="text" placeholder="Enter Title" onChange={(event) => setTitle(event.target.value)}></input>
            <input type="text" placeholder="Enter Description" onChange={(event) => setDescription(event.target.value)}></input>
            <input type="text" placeholder="Enter Price in USD" onChange={(event) => setPrice(event.target.value)}></input>
            <input type="text" placeholder="Enter Location" onChange={(event) => setLocation(event.target.value)}></input>
            <p>Will Deliver?:</p>
            <input type="checkbox" placeholder="true" onChange={(event) => setWillDeliver(event.target.checked)}></input>
            <div>
            <button type='submit'>Create New Post</button>
            </div>
        </form>
    )
}
export default CreatePost
